'    -- Functions',
'    /**',
'     * Escapses reseverd characters in JSON value',
'     * @return Escaped JSON value',
'     */',
'    FUNCTION f_get_escaped_json_value (p_json IN VARCHAR2)',
'      RETURN VARCHAR2',
'    IS',
'      l_json   VARCHAR2 (32767);',
'    BEGIN',
'      l_json := p_json;',
'      l_json := REPLACE (l_json, ''\'', ''\\'');',
'      l_json := REPLACE (l_json, ''"'', ''\"'');',
'      RETURN l_json;',
'    END f_get_escaped_json_value;',
'    ',
'  BEGIN',
'    -- During plug-in development it''s very helpful to have some debug information',
'    IF APEX_APPLICATION.g_debug THEN',
'      apex_plugin_util.debug_dynamic_action ',
'      ( p_plugin         => p_plugin',
'      , p_dynamic_action => p_dynamic_action',
'      );',
'    END IF;',
'  ',
'    -- Load JS Files',
'    apex_javascript.add_library ',
'    ( p_name      => ''$console_wrapper_''',
'    , p_directory => p_plugin.file_prefix',
'    , p_version   => ''1.0.1''',
'    );',
'    ',
'    apex_javascript.add_library ',
'    ( p_name      => ''ircolumngroups''',
'    , p_directory => p_plugin.file_prefix',
'    , p_version   => NULL',
'    );',
'  ',
'    -- Generate the JSON object',
'    l_json := ''{"columns": ['';',
'  ',
'    FOR x IN ',
'    ( SELECT ROWNUM rn, column_alias, column_group',
'      FROM   APEX_APPLICATION_PAGE_IR_COL',
'      WHERE  application_id = l_app_id',
'      AND    page_id        = l_page_id',
'      AND    column_group IS NOT NULL',
'    ) LOOP',
'      IF x.rn != 1 THEN',
'        l_json := l_json || '','';',
'      END IF;',
'  ',
'      l_json :=',
'        l_json || ''{"column_alias":"'' || x.column_alias||''"''',
'               || '',"column_group":"'' ',
'               || f_get_escaped_json_value(APEX_APPLICATION.do_substitutions(x.column_group)) ',
'               || ''"}'';',
'    END LOOP;',
'  ',
'    l_json := l_json || '']}'';',
'  ',
'    -- Set Attributes',
'    l_result.attribute_01 := l_json;',
'    l_result.attribute_02 := ''ClariFit_irColGrpRow'';',
'    l_result.attribute_03 := l_disp_border;',
'  ',
'    -- Set JS function to be executed',
'    l_result.javascript_function := ',
'           ''function(){''||',
'              ''var that = this;''||',
'              ''if(this.browserEvent==="load"){''||',
'                ''setTimeout(function() {''||',
'                  ''apex.jQuery(that.triggeringElement).irColumnGroups({''||',
'                     ''"id": apex.jQuery(that.triggeringElement).attr("id"),''||',
'                     ''"showBorder": ''||l_disp_border||'',''||',
'                     ''"colGrps": ''||l_json||'',''||',
'                     ''"imgPrefix": "''||v(''IMAGE_PREFIX'')||''"''||',
'                  ''}); ''||',
'                ''},''||l_delay||'');''||',
'              ''}else{''||',
'                  ''apex.jQuery(that.triggeringElement).irColumnGroups({''||',
'                     ''"id": apex.jQuery(that.triggeringElement).attr("id"),''||',
'                     ''"showBorder": ''||l_disp_border||'',''||',
'                     ''"colGrps": ''||l_json||'',''||',
'                     ''"imgPrefix": "''||v(''IMAGE_PREFIX'')||''"''||',
'                  ''});''||',
'              ''};''||   ',
'           ''}'';    ',
'    RETURN l_result;',
'  ',
'  END render_plugin;',
''))
