,p_supported_component_types=>'APEX_APPLICATION_PAGE_ITEMS'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('DYNAMIC ACTION','COM.AF.PLUGIN.JQ.IRFREEZEHEADER'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'-- =============================================================================',
'--',
'--   ____________________________  __    __________                ______                                ',
'--   ___    |__  __ \__  ____/_  |/ /    ___  ____/___________________  /_____ __________________________',
'--   __  /| |_  /_/ /_  __/  __    /     __  /_   __  ___/  _ \  _ \_  /_  __ `/_  __ \  ___/  _ \_  ___/',
'--   _  ___ |  ____/_  /___  _    |      _  __/   _  /   /  __/  __/  / / /_/ /_  / / / /__ /  __/  /    ',
'--   /_/  |_/_/     /_____/  /_/|_|      /_/      /_/    \___/\___//_/  \__,_/ /_/ /_/\___/ \___//_/     ',
'--                                                                                                    ',
'--',
'-- =============================================================================',
'--',
'-- Plugin      : com.af.plugin.jq.irfreezeheader',
'-- Description : This is a dynamic action plugin to freeze Interactive Report',
'--               table headers.',
'--               http://www.farinspace.com/jquery-scrollable-table-plugin/',
'--',
'-- Author      : Matt Nolan',
'-- Email       : matt.nolan@theapexfreelancer.com',
'-- Twitter     : apex_freelancer',
'-- Webiste     : www.theapexfreelancer.com',
'-- Created     : 18-Jul-2011',
'--',
'-- =============================================================================',
'--',
'-- Change Log',
'--',
'-- Date         Author              Description',
'-- ------------ ------------------- --------------------------------------------',
'-- 18-Jul-2011  Matt Nolan          v1.0 - Plugin Created.',
'-- 02-Oct-2012  Matt Nolan          v1.2 - Fix for misalignment of columns',
'--                                       - Added new strecthing option',
'--',
'-- =============================================================================',
'',
'  FUNCTION render_plugin',
'  ( p_dynamic_action  in apex_plugin.t_dynamic_action',
'  , p_plugin          in apex_plugin.t_plugin',
'  ) RETURN apex_plugin.t_dynamic_action_render_result ',
'  AS',
'  ',
'    l_height                VARCHAR2(100) := coalesce(p_dynamic_action.attribute_01, ''400'');',
'    l_width                 VARCHAR2(100) := coalesce(p_dynamic_action.attribute_02, ''undefined'');',
'    l_delay                 VARCHAR2(100) := coalesce(p_dynamic_action.attribute_03, ''500'');',
'    l_result                apex_plugin.t_dynamic_action_render_result;',
'    l_stretch               VARCHAR2(100) := coalesce(p_dynamic_action.attribute_04, ''N'');',
'',
'  BEGIN',
'    --',
'    -- Debug information',
'    --',
'    IF apex_application.g_debug THEN',
'      apex_plugin_util.debug_dynamic_action',
'      ( p_plugin         => p_plugin',
'      , p_dynamic_action => p_dynamic_action',
'      );',
'    END IF;',
'',
'    --',
'    -- If we are stretching the unfrozen container we specify a setting of auto',
'    --',
'    IF l_stretch = ''Y'' THEN',
'      l_width := ''"auto"'';',
'    END IF;',
'',
'    --',
'    -- Include CSS.',
'    --',
'    apex_css.add_file ',
'    ( p_name      => ''jquery.tablescroll''',
'    , p_directory => p_plugin.file_prefix',
'    , p_version   => NULL',
'    );',
'    --',
'    -- Include Javascript.',
'    --',
'    apex_javascript.add_library ',
'    ( p_name      => ''jquery.tablescroll''',
'    , p_directory => p_plugin.file_prefix',
'    , p_version   => NULL',
'    ) ;',
'    apex_javascript.add_library ',
'    ( p_name      => ''irfreezeheader''',
'    , p_directory => p_plugin.file_prefix',
'    , p_version   => NULL',
'    ) ;',
'    --',
'    -- On page load we allow the user to set a delay in case there are images',
'    -- in the report that need to be laoded first. If there isn''t a delay value ',
'    -- set and there are images there''s a good possibility that the headers ',
'    -- columns and row data columns will be mis-aligned',
'    --',
'    l_result.javascript_function := ',
'           ''function(){''||',
'              ''var that = this;''||',
'              ''if(this.browserEvent==="load"){''||',
'                ''setTimeout(function() {''||',
'                  ''var noWidth = ($(that.triggeringElement).data("irFreezeCols"));''||',
'                  ''apex.jQuery(that.triggeringElement).irFreezeHeader({''||',
'                     ''"id": apex.jQuery(that.triggeringElement).attr("id"),''||',
'                     ''"height": ''||l_height||'',''||',
'                     ''"width": (noWidth) ? null : ''||l_width||'',''||',
'                     ''"imgPrefix": "''||v(''IMAGE_PREFIX'')||''"''||',
'                  ''}); ''||',
'                ''},''||l_delay||'');''||',
'              ''}else{''||',
'                  ''var noWidth = ($(that.triggeringElement).data("irFreezeCols"));''||',
'                  ''apex.jQuery(that.triggeringElement).irFreezeHeader({''||',
'                     ''"id": apex.jQuery(that.triggeringElement).attr("id"),''||',
'                     ''"height": ''||l_height||'',''||',
'                     ''"width": (noWidth) ? null : ''||l_width||'',''||',
'                     ''"imgPrefix": "''||v(''IMAGE_PREFIX'')||''"''||',
'                  ''});''||',
'              ''};''||   ',
'           ''}'';    ',
'    RETURN l_result;',
'    ',
'  END render_plugin;',
''))
,p_api_version=>1
,p_render_function=>'render_plugin'
,p_standard_attributes=>'REGION'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'<p>',
'	<strong>APEX Freelancer - IR Freeze Header</strong></p>',
'<p>',
'	<strong>Plug-in Type</strong>: Dynamic Action<br />',
'	<strong>Summary</strong>: On long reports this plugin allows the ability to freeze the column headers in place. It works best in conjunction with the Freeze Columns plugin.</p>',
'<div>',
'	<div>',
'		<strong>Documentation URL: </strong><a href="http://www.theapexfreelancer.com/apex/f?p=APEXFREELANCER:IR_PLUGIN_PACKAGE:0" target="_blank">http://www.theapexfreelancer.com/apex/f?p=APEXFREELANCER:IR_PLUGIN_PACKAGE:0</a></div>',
'	<div>',
'		&nbsp;</div>',
'	<div>',
'		<strong>Licensing: </strong>This plugin requires the purchase of an appropriate commercial license. Please visit the following URL for more <a href="http://www.theapexfreelancer.com/apex/f?p=APEXFREELANCER:IR_PLUGIN_PACKAGE:0:LICENSE" target="_blan'
||'k">licensing</a> information.</div>',
'	<div>',
'		&nbsp;</div>',
'	<div>',
'		<a href="http://www.theapexfreelancer.com/" target="_blank"><img alt="APEX Freelancer" src="http://www.theapexfreelancer.com/c/af-logo.png" style="width: 125px; height: 110px;" /></a></div>',
'</div>',
''))
,p_version_identifier=>'1.2.2'
,p_about_url=>'http://www.theapexfreelancer.com/apex/f?p=APEXFREELANCER:IR_PLUGIN_PACKAGE:0'
);
wwv_flow_api.create_plugin_attribute(
 p_id=>wwv_flow_api.id(73876876215969982)
,p_plugin_id=>wwv_flow_api.id(73876663055966212)
,p_attribute_scope=>'COMPONENT'
,p_attribute_sequence=>1
,p_display_sequence=>10
,p_prompt=>'Height'
,p_attribute_type=>'NUMBER'
,p_is_required=>true
,p_default_value=>'400'
,p_display_length=>5
,p_is_translatable=>false
,p_help_text=>'<p>The height of the report data container. A vertical scrollbar will be displayed on the report data for any overflow. <strong>Note:</strong> this must be a number i.e. "px" or "%" is not permitted.</p>'
);
wwv_flow_api.create_plugin_attribute(
 p_id=>wwv_flow_api.id(67982447120162460)
,p_plugin_id=>wwv_flow_api.id(73876663055966212)
,p_attribute_scope=>'COMPONENT'
,p_attribute_sequence=>2
,p_display_sequence=>20
,p_prompt=>'Width'
,p_attribute_type=>'INTEGER'
,p_is_required=>false
,p_display_length=>5
,p_is_translatable=>false
,p_depending_on_attribute_id=>wwv_flow_api.id(90201825253576888)
,p_depending_on_has_to_exist=>true
,p_depending_on_condition_type=>'EQUALS'
,p_depending_on_expression=>'N'
,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'<p>The width of the report, if left null the report width will default to it''s natural display width.</p>',
'<p><strong>Note: </strong> this setting will be ignored if you use the plugin in combination with the Freeze columns plugin. The Freeze columns plugin determines the width of the report.',
'</p>'))
);
wwv_flow_api.create_plugin_attribute(
 p_id=>wwv_flow_api.id(67997832021994023)
,p_plugin_id=>wwv_flow_api.id(73876663055966212)
,p_attribute_scope=>'COMPONENT'
,p_attribute_sequence=>3
,p_display_sequence=>30
,p_prompt=>'Delay on Page Load'
,p_attribute_type=>'INTEGER'
,p_is_required=>true
,p_default_value=>'500'
,p_display_length=>5
,p_is_translatable=>false
,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'The delay on page load before the plugin is initiated. This is required for rows which have images in them. The header column widths and row column widths will be calculated incorrectly if the images haven''t been loaded on the page. Use this delay to'
||' ensure the images have been loaded before the heights are calculated. If no images are in use in the row then set the value to zero.',
'<br />',
'<br />',
'<strong>Note:</strong> this delay setting does not apply if you have bound the plugin to another dynamic action event e.g. "IR Freeze Columns - End". It only applies on page load.'))
);
wwv_flow_api.create_plugin_attribute(
 p_id=>wwv_flow_api.id(90201825253576888)
,p_plugin_id=>wwv_flow_api.id(73876663055966212)
,p_attribute_scope=>'COMPONENT'
,p_attribute_sequence=>4
,p_display_sequence=>15
,p_prompt=>'Stretch Unfrozen Columns?'
,p_attribute_type=>'CHECKBOX'
,p_is_required=>true
,p_default_value=>'N'
,p_is_translatable=>false
,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'<p>When set to "Yes" the report container will be stretched to the browser screen width minus any offsets/padding of the parent region.',
'</p>',
'<p>When set to "No" you will be required to specify the width of the container using another plugin attribute.',
'</p>',
'<p><strong>Note:</strong> it is only recommended that you set to "Yes" for IR report regions which do not have any regions positioned to the left of the interactive report. e.g. navigation list. It is because this setting uses an offset from the left'
||' side of the page to calculate the right side offset.',
'</p>'))
);
wwv_flow_api.create_plugin_event(
 p_id=>wwv_flow_api.id(73884663850149383)
,p_plugin_id=>wwv_flow_api.id(73876663055966212)
,p_name=>'end'
,p_display_name=>'IR Freeze Header - End'
);
wwv_flow_api.create_plugin_event(
 p_id=>wwv_flow_api.id(73884360387148312)
,p_plugin_id=>wwv_flow_api.id(73876663055966212)
,p_name=>'start'
,p_display_name=>'IR Freeze Header - Start'
);
wwv_flow_api.component_end;
end;
